# --- Importing modules ---

import logging
from aiogram import Bot, Dispatcher, executor, types
from aiogram.contrib.fsm_storage.memory import MemoryStorage
import sqlite3
import markup as nav
import parser

# --- idk ---
bot = Bot(token="TOP SECRET")
dp = Dispatcher(bot, storage=MemoryStorage())
logging.basicConfig(level=logging.INFO)

connection = sqlite3.connect("database.db")
cursor = connection.cursor()


# --- Command /start, and connecting database ---
@dp.message_handler(commands=['start'])
async def process_start_command(message: types.Message):
    cursor.execute("""CREATE TABLE IF NOT EXISTS users (
            user_id INT PRIMARY KEY,
            lang INT
    )""")

    await message.reply("üá∑üá∫ –ü—Ä–∏–≤–µ—Ç! –í—ã–±–µ—Ä–∏—Ç–µ —è–∑—ã–∫. \n\nüá∫üá∏ Hello! Select the language. \n\nüáπüáØ –°–∞–ª–æ–º! –ó–∞–±–æ–Ω—Ä–æ –∏–Ω—Ç–∏—Ö–æ–± –∫—É–Ω–µ–¥. \n\nüá∫üáø Salom! Tilni tanlang.", reply_markup=nav.langMenu)

    cursor.execute(f"INSERT INTO users VALUES({message.chat.id}, 0)")
    connection.commit()


